// Test :: Mixin :: Min
/* globals describe: true, it: true */
'use strict';

describe('seed-breakpoints: mixin: min', function() {
  it('should accept breakpoint sizes (defined in _config.scss)', function() {
    var style = `
      @import "./_index";
      .machi {
        @include breakpoint-min(lg) {
          position: absolute;
        }
      }
    `;
    var output = barista({ content: style });
    var rule = output.rule('.machi');
    var mq = rule.mq();

    expect(rule.exists()).to.be.true;
    expect(mq.rule).to.include('min-width: 992'); // lg size + 1
    expect(mq.rule).to.not.include('max');
    expect(mq.props.length).to.equal(1);
  });

  it('should accept numerical sizes (e.g. px)', function() {
    var style = `
      @import "./_index";
      .machi {
        @include breakpoint-min(80em) {
          position: absolute;
        }
      }
    `;
    var output = barista({ content: style });
    var rule = output.rule('.machi');
    var mq = rule.mq();


    expect(rule.exists()).to.be.true;
    expect(mq.rule).to.include('min-width: 80em');
    expect(mq.rule).to.not.include('max');
    expect(mq.props.length).to.equal(1);
  });
});
